INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7618, 'Soft pareo towel in a combination of 130g/m2 recycled cotton on the front side and 125g/m2 polyester at the back. Available in a wide range of two-tone colours, with side label. Presented in an individual bag.', '20753', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7619, 'Mochila pareo de 180x90cm en resistente algodón reciclado de 170g/m2 en variados colores. Con plegado especial que la convierte en mochila de cuerdas para cómodo transporte. Con logotipo algodón reciclado en etiqueta exterior. Presentado en bolsa individual.', '20754', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7620, '180x90cm pareo backpack, manufactured in resistant recycled cotton 170g/m2 and in different colours. With a special folding system for easy transport that turns it into a drawstring backpack . Recycled cotton label on outside and presented in an individual bag.', '20754', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7621, 'Bolsa fabricada en algodón reciclado de alto gramaje 220g/m2. De acabado cosido y con asas largas reforzadas. Con fuelle y disponible variados colores. Logotipo algodón reciclado en etiqueta exterior. Resistencia hasta 9 kg de peso.', '20755', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7622, 'Bag made of recycled cotton and heavyweight 220g/m2. Stitched finish and with long reinforced handles. With gusset and available in different colours. Recycled cotton label on the outside, with resistance up to 9 kg in weight.', '20755', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7623, 'Bolsa fabricada en resistente algodón de 280g/m2. De acabado cosido, con fuelle y asas largas de 50cm reforzadas, de color blanco en forma de cuerda. Disponible en variada gama de colores bicolor, con resistencia hasta 10kg de peso.', '20756', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7624, 'Bag made of strong 280g/m2 cotton. Stitched finish, with gusset and 50cm length reinforced handles in rope style t. Available in a wide range of two-tone colours, with resistance up to 10kg in weight.', '20756', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7625, 'Bolsa fabricada en resistente algodón de 280g/m2. De acabado cosido, cierre de botón, fuelle y asas largas de 60cm reforzadas, en diversos colores. Además, tiene bolsillo exterior de fácil acceso y otro interior con cierre de cremallera. Resistencia hasta 10kg de peso.', '20757', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7626, 'Bag made of resistant 280g/m2 cotton. Stitched finish, with button closure, gusset and 60cm length reinforced handles, available in a variety of colours. Including an easy-access outside pocket and an inside pocket with zip closure. Resistant up to 10kg in weight.', '20757', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7627, 'Bolsa fabricada en resistente algodón de 450g/m2 y base de yute. De acabado cosido, cierre de cremallera, con distinguido diseño a rayas blancas y azules. Asas largas hechas en algodón de color blanco en forma de cuerda reforzadas con argollas. Resistencia hasta 13kg de peso.', '20758', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7628, 'Bag made of resistant 450g/m2 cotton, with jute base. Stitched finish and zip closure, with distinguished blue and white striped design. Long handles made of white cotton in rope style, reinforced with eyelets. Resistance up to 13kg in weight.', '20758', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7629, 'Bolsa de paja en forma rectangular y de desenfadado diseño.Gran capacidad y con originales asas largas en polipiel. Con amplio parche exterior en polipiel, especialmente diseñado para personalización en láser y a juego con las asas.', '20759', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7630, 'Straw bag in rectangular shape and casual design. With large capacity and original long PU leather handles. Large PU leather patch outside, specially designed for laser engraving and matching with the handles.', '20759', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7631, 'Portatodo impermeable fabricado en resistente pvc. Cierre de seguridad por cremallera con asa de transporte con mosquetón de plástico giratorio. Disponible en variada gama de colores con cremallera y asa de color negro.', '20760', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7632, 'Waterproof multi-purpose bag made of resistant PVC. Security zip closure, with carry handle and swivel carabiner in plastic. Available in a variety of colours, with black zip and handle.', '20760', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7633, 'Funda para gafas en suave microfibra RPET y amplia gama de divertidos colores, con cordones autocierre. Con logotipo en etiqueta. El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20761', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7634, 'Eyewear pouch in soft RPET microfibre.Available in a wide range of fun colours, with self-closing strings and RPET label. RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20761', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7635, 'Abanico fabricado con varillas de madera y tela en algodón en combinación con el poliéster. De color natural y presentado en caja individual diseño kraft.', '20762', null);
