INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7637, 'Abanico fabricado con varillas de bambú y tela de poliéster. En tonos naturales y presentado en caja individual diseño kraft.Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápidos, siendo una excelente alternativa al uso de madera tradicional.', '20763', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7638, 'Fan made with bamboo ribs and polyester fabric. In natural tones and presented in an individual kraft design box.Materials such as bamboo encourage the use of natural raw materials, reducing pollutant emissions. It comes from a highly resistant and versatile plant with rapid growth and regeneration, making of it an excellent alternative to traditional wood.', '20763', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7639, 'Gafas de sol fabricadas en bambú con protección UV400. Montura de colores en resistente material PC y patillas en bambú. Lentes AC (acetato de celulosa) de acabado espejo, disponibles en variada gama de colores en combinación con la montura. Presentada en atractiva bolsa de papel diseño kraft.Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápidos, siendo una excelente alternativa al uso de madera tradicional.', '20764', null);
