INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7641, 'Ventilador portátil fabricado en resistente ABS. Cuenta con tres velocidades y soporte integrado para smartphone. Se alimenta mediante una batería de 300 mAh, con indicadores de luz que muestran el nivel de carga y cable Micro USB incluido. Presentado individualmente en caja de diseño kraft.', '20765', null);
