INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7643, 'Ventilador portátil fabricado en resistente ABS. Este práctico dispositivo cuenta con tres velocidades, con un lanyard para un uso cómodo. Se alimenta mediante una batería de 400 mAh, compatible con conexiones USB , y presenta indicadores de luz para conocer el nivel de carga de la batería. Presentado individualmente en caja de diseño kraft.', '20766', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7644, 'Portable fan made of durable ABS. With three speeds and integrated smartphone holder. Powered by a 300 mAh battery, with charge level indicator lights and Micro USB cable included. Presented individually in a kraft design box.', '20766', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7645, 'Práctico ventilador portátil fabricado en resistente ABS. Este elegante ventilador tiene un diseño en forma de collar para colocarlo alrededor del cuello, brindando una sensación refrescante. Ofrece tres posiciones de velocidad diferentes y es recargable mediante su batería de 800 mAh, cable Micro USB incluido. Presentado en caja de diseño kraft.', '20767', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7646, 'Handy portable fan made of durable ABS. This stylish fan has been designed to wear around the neck, providing a cooling sensation. It offers three different speed settings and is rechargeable via its 800 mAh battery, Micro USB cable included. Presented in a kraft design box.', '20767', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7647, 'Ventilador vaporizador de 35 ml de capacidad. Presento con cuerpo translúcido y aspas de EVA. Se alimenta con 1 pilas AA (no incluidas). Disponible en colores variados, con un práctico mosquetón a juego. Presentado individualmente en caja.', '20769', null);
