INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7827, 'Set gamer para una experiencia de videojuego inmersiva. Incluye ratón óptico y teclado QWERTY en español, ambos retroiuminados con luz LED multicolor y conexión mediante cable USB. Además, incorpora una alfombrilla en suave poliéster, con base de silicona antideslizante perfecta para usar el ratón con precisión. El set lo completan unos auriculares de diadema con sonido envolvente y retroiluminados con luz LED multicolor; micrófono integrado y conexión jack de 3,5mm. Set en elegante acabado de color negro, presentado en caja individual de diseño kraft.', '20863', null);
