INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7933, 'Elegante carpeta archivadora fabricada en corcho natural. Con bolsillo interior grande para folios, 4 portatarjetas, elástico para bolígrafo y bloc de 20 hojas con disposición a una raya. Cierre mediante una banda elástica en su exterior. Presentado individualmente en bolsa reciclada. Elaborado con elementos vegetales para así fomentar la utilización de materias primas naturales y reducir las emisiones contaminantes.', '20917', null);
