INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7949, 'Pinza porta notas fabricada en bambú. Con potente imán para poner en superficies metálicas, de diseño bicolor en blanco y madera bambú. Presentado individualmente en una bolsa. Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápidos, siendo una excelente alternativa al uso de madera tradicional.', '20925', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7950, 'Clip for notes made of bamboo. With strong magnet to attach to metal surfaces, and two-tone design in white and bamboo wood. Presented in an individual bag.Materials such as bamboo promote the use of natural raw materials, thus reducing pollutant emissions. It comes from a highly resistant and versatile plant, with rapid growth and regeneration, making it an excellent alternative to the use of traditional wood.', '20925', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7951, 'Lapicero con soporte fabricado en bambú y acero inox. De diseño minimalista, con base fabricada en bambú y estructura metálica. Presentado en una caja de diseño kraft. Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápidos, siendo una excelente alternativa al uso de madera tradicional.', '20926', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7952, 'Pen holder with stand made of bamboo and stainless steel. Minimalist design, with bamboo base and metal structure. Presented in a kraft design box.Materials such as bamboo promote the use of natural raw materials, thus reducing pollutant emissions. It comes from a highly resistant and versatile plant, with rapid growth and regeneration, making it an excellent alternative to the use of traditional wood.', '20926', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7953, 'Útil funda de ordenador portátil hecha en algodón reciclado. Compartimento principal acolchado de hasta 14 pulgadas fabricado en algodón 100% reciclado, con etiqueta distintiva. Disponible en elegantes colores y presentado en una bolsa individual reciclada. Elaborado con elementos vegetales para así fomentar la utilización de materias primas naturales y reducir las emisiones contaminantes.', '20927', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7954, 'Useful laptop sleeve made of Recycled cotton. Padded main compartment for laptops up to 14 inches and made in 100% Recycled cotton, with a distinctive label. Available in stylish colours and presented in an individual Recycled bag.Made with plant-based elements to promote the use of natural raw materials and reduce polluting emissions.', '20927', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7955, 'Lanyard fabricado en resistente algodón 100% y bambú. Accesorio circular especial para la personalización de bambú y cierre de seguridad en cuello fabricado en caña de trigo, con mosquetón metálico giratorio.Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápidos, siendo una excelente alternativa al uso de madera tradicional.', '20929', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7956, 'Lanyard made of resistant 100% cotton and bamboo. Special round accessory for customisation made of bamboo, and security neck closure made of wheat straw. With swivel metal carabiner.Materials such as bamboo promote the use of natural raw materials, thus reducing pollutant emissions. It comes from a highly resistant and versatile plant, with rapid growth and regeneration, making it an excellent alternative to the use of traditional wood.', '20929', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7957, 'Lanyard Rainbow multicolor, fabricado en suave poliéster. Con mosquetón metálico giratorio y accesorio de seguridad en el cuello. Todo un orgullo de lanyard.', '20930', null);
