INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8115, 'Práctico llavero multiherramienta, con accesorios fabricados en resistente acero inox y carcasa en madera natural. Con 3 funciones: tijeras, cuchillo y lima. Presentado en caja individual de diseño kraft.', '21009', null);
