INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8117, 'Práctico llavero navaja con hoja de acero inox y resistente empuñadura metálica con detalles en madera natural. Presentado en caja de diseño kraft.', '21010', null);
