INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8231, 'Calculadora de 10 dígitos fabricada corcho. Con mecanismo de apagado automático y suaves teclas. Funciona con energía solar, con alimentación auxiliar mediante 1 pila de botón (incluida). Presentada en atractiva caja de diseño kraft, con manual de instrucciones disponible en español e inglés.Elaborada con elementos vegetales para así fomentar la utilización de materias primas naturales y reducir las emisiones contaminantes.', '21070', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8232, '10-digit calculator made of cork. With automatic switch-off mechanism and soft keys. Solar powered, with auxiliary power supply by 1 button cell battery (included). Presented in an attractive kraft design box, with instructions manual available in English and Spanish.Made with plant-based elements to promote the use of natural raw materials and reduce polluting emissions.', '21070', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8233, 'Bloc de notas con tapas rígidas fabricadas en RPET. Interior con 80 hojas lisas. Incluye marcapáginas y elástico de cierre, con logotipo RPET en la contraportada. Disponible en elegantes colores y presentado individualmente en bolsa reciclada. El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '21071', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8234, 'Notepad with hard covers made of RPET. Inside with 80 plain sheets. Includes bookmark and elastic closure, with RPET logo on the back cover. Available in stylish colours and presented individually in a recycled bag.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '21071', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8235, 'Original monedero en forma de mochila, con bolsillo de cierre de cremallera, cintas y con práctico mosquetón para poder enganchar fácilmente. Fabricado en resistente poliéster 600D y disponible en varios colores.', '21072', null);
