INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8242, 'Stylish ball pen pointer made of cork and Aluminium, with push-button mechanism. Durable matte finish Aluminium barrel and cork grip with black pointer on top and in grey color. Metal clip and presented in individual recycled bag.Made with plant-based elements to promote the use of natural raw materials and reduce polluting emissions.', '21075', null);
