INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8244, '4-in-1 ball pen with push-button retractable mechanism. Combination of natural recycled cardboard barrel and accessories in wheat straw and ABS, to promote the use and the recycling of raw materials, thus reducing pollutant emissions. With 4 cartridges in blue, red, black and green ink.Recycled cardboard comes from the reuse of manufacturing surpluses and paper recycling at the end of its lifespan, making it an ecological and sustainable canvas.', '21076', null);
