INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8250, 'Stylish Aluminium ball pen with push-button mechanism. Barrel with matt rubberised finish and bamboo accents, available in a wide range of distinctive colours. Metal clip, jumbocartridge and blue ink. Presented in an individual recycled bag.Materials such as bamboo promote the use of natural raw materials, thus reducing pollutant emissions. It comes from a highly resistant and versatile plant, with rapid growth and regeneration, making it an excellent alternative to the use of traditional wood.', '21079', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8251, 'Bolígrafo con cuerpo en bambú y papel reciclado. Caña fabricada en suave bambú con empuñadura hecha en cartón reciclado con textura y en variados colores. Clip metálico y accesorios en cromado. De mecanismo pulsador, de cartucho jumbo y con tinta azul.Elaborado con elementos vegetales para así fomentar la utilización de materias primas naturales y reducir las emisiones contaminantes.', '21080', null);
