INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8353, 'Camiseta para niño fabricada en material 60% algodón reciclado en combinación con RPET, tejido Single Jersey de 150g/m2. Disponible en variada gama de colores, con logotipo reciclado en etiqueta y en tallas 4-5, 6-8, 10-12.El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '21139', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8354, 'Kid T-shirt made of 60% recycled cotton in combination with RPET, single jersey fabric, 150g/m2. Available in a wide range of colours, with recycled logo on the label and in sizes 4-5, 6-8, 10-12.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '21139', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8355, 'Camiseta de adulto confeccionada en 100% algodón peinado Ring Spun y estilo Single Jersey de 160g/m2. Tacto suave y corte moderno, confeccionada en tejido tubular sin costuras laterales. Cuello redondo estrecho en canalé 2x2. Doble costura en dobladillo y mangas, para una mayor durabilidad y resistencia al uso intensivo. Disponible en gama de colores de moda y en tallas S, M, L, XL, XXL y 3XL.', '21140', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8356, 'Adult T-shirt made of 100% Ring Spun combed cotton and 160g/m2 Single Jersey style. Soft feel and modern cut, made in tubular fabric without side seams. Narrow round neck in 2x2 rib. Double stitched hem and sleeves, for extra durability and resistance to intensive use. Available in a range of fashionable colours and in sizes S, M, L, XL, XXL and 3XL.', '21140', null);
