INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8358, 'Woman T-shirt made of 100% Ring Spun combed cotton and 160g/m2 Single Jersey style. Soft feel and modern cut, with side seams. Narrow round neck in 2x2 rib. Double stitched hem and sleeves, for extra durability and resistance to intensive use. Available in a range of fashionable colours and in sizes S, M, L, XL y XXL.', '21141', null);
