INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8360, 'Kid T-shirt made of 100% Ring Spun combed cotton and 160g/m2 Single Jersey style. Soft feel and modern cut, made in tubular fabric without side seams. Narrow round neck in 2x2 rib. Double stitched hem and sleeves, for extra durability and resistance to intensive use. Available in a range of fashionable colours and in sizes 4-5, 6-8, 10-12.', '21142', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8361, 'Polo de adulto de manga corta confeccionado en piqué 100% algodón peinado Ring Spun de 220g/m2. Tacto suave y corte moderno, con aperturas laterales en los bajos y disponible en variada gama de colores. Tapeta de dos botones, con doble costura en dobladillo y mangas, para una mayor durabilidad y resistencia al uso intensivo. Disponible en tallas S, M, L, XL, XXL y 3XL.', '21143', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8362, 'Adult short sleeve polo shirt, made of 100% combed cotton pique Ring Spun, 220g/m2. Soft feel and modern cut, with side openings at the hem and available in a wide range of colours. Two-button placket, with double-stitched hem and sleeves for extra durability and resistance to intensive use. Available in sizes S, M, L, XL, XXL and 3XL.', '21143', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8363, 'Polo de mujer de manga corta. Confeccionado en piqué 100% algodón peinado Ring Spun de 220g/m2. Tacto suave y corte moderno, con aperturas laterales en los bajos y disponible en variada gama de colores. Tapeta de dos botones, con doble costura en dobladillo y mangas, para una mayor durabilidad y resistencia al uso intensivo. Disponible en tallas S, M, L, XL y XXL.', '21144', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8364, 'Woman short sleeve polo shirt, made of 100% combed cotton pique Ring Spun, 220g/m2. Soft feel and modern cut, with side openings at the hem and available in a wide range of colours. Two-button placket, with double-stitched hem and sleeves for extra durability and resistance to intensive use. Available in sizes S, M, L, XL y XXL.', '21144', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8365, 'Polo de piqué transpirable en suave material 100% poliéster de 180g/m2. Con tres botones de cierre a juego y aperturas laterales en los bajos. Disponible en variada gama de colores, con detalles en contraste en cuello y mangas. Disponible en tallas XS, S, M, L, XL, XXL.', '21145', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8366, 'Breathable piqué polo shirt in soft 100% polyester 180g/m2 material. With three matching button fastening and side openings at the hem. Available in a wide range of colours, with contrasting details on collar and sleeves. Available in sizes XS, S, M, L, XL, XXL.', '21145', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8367, 'Chaleco resistente al viento y al agua, en cálida combinación de materiales soft shell y poliéster, con interior acolchado. De elegante construcción y en variada gama de colores, con detalles y laterales en negro. Cierre de cremallera y bolsillos laterales de cremallera, con tiradores para fácil apertura. Disponible en tallas S, M, L, XL, XXL.', '21146', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8368, 'Wind and water resistant vest in a warm combination of soft shell and polyester materials, with padded interior. Elegant construction in a wide range of colours, with black details and sides. Zip fastening and side zip pockets with pullers for easy opening. Available in sizes S, M, L, XL, XXL.', '21146', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8369, 'Chaleco unisex en cálido y suave material 100% RPET, con capucha ajustable mediante cintas elásticas. En material extra ligero efecto pluma, resistente al agua y cortavientos. Con acolchado interior, cremallera en cuello con tapeta y cinta en tirador, además de amplios bolsillos laterales con cremallera. Incluye práctica funda de transporte con cordón ajustable y etiqueta con logotipo RPET. Disponible en variada gama de colores y en tallas XS, S, M, L, XL, XXL.El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '21147', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8370, 'Unisex vest in warm and soft 100% RPET material, with adjustable hood with elastic straps. In extra light feather effect material, water resistant and windproof. With inner padding, zip on collar with placket and strap on puller, as well as large side pockets with zip. Including a practical carry pouch with adjustable drawstring and RPET logo label. Available in a wide range of colours and sizes XS, S, M, L, XL, XXL.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '21147', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8371, 'Chaqueta unisex en cálido y suave material 100% RPET, con capucha ajustable mediante cintas elásticas. En material extra ligero efecto pluma, resistente al agua y cortavientos. Con acolchado interior, cremallera en cuello con tapeta y cinta en tirador, además de amplios bolsillos laterales con cremallera. Incluye práctica funda de transporte con cordón ajustable y etiqueta con logotipo RPET. Disponible en variada gama de colores y en tallas XS, S, M, L, XL, XXL.El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '21148', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8372, 'Unisex jacket in warm and soft 100% RPET material, with adjustable hood with elastic straps. In extra light feather effect material, water resistant and windproof. With inner padding, zip on collar with placket and strap on puller, as well as large side pockets with zip. Including a practical carry pouch with adjustable drawstring and RPET logo label. Available in a wide range of colours and sizes XS, S, M, L, XL, XXL.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '21148', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8373, 'Camiseta técnica para adulto en material RPET transpirable de 135g/m2 y teñido Space Dye. Con manga ranglán, cuello redondo y etiqueta con logotipo RPET. Disponible en variada gama de colores Space Dye y en tallas XS, S, M, L, XL, XXL.El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '21149', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8374, 'Adult technical T-shirt in breathable 135g/m2 RPET material and Space Dye. With raglan sleeves, round neck and RPET logo label. Available in a wide range of colours Space Dye and in sizes XS, S, M, L, XL, XXL.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '21149', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8375, 'Camiseta técnica para adulto en poliéster de 135g/m2, transpirable y de suave tacto. Con innovador estampado a rayas y una zona lisa en el pecho para personalización. Con manga ranglán y cuello redondo. Disponible en variada gama de colores y en tallas XS, S, M, L, XL, XXL.', '21150', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8376, 'Adult technical T-shirt in 135g/m2 polyester, breathable and with a soft touch. With innovative striped print and a blank area on the chest for branding. With raglan sleeves and round neck. Available in a wide range of colours and in sizes XS, S, M, L, XL, XXL.', '21150', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8377, 'Camiseta técnica para adulto con innovador y original estampado. Confeccionada en poliéster de 135g/m2, transpirable y de suave tacto. Manga ranglán, cuello redondo y disponible en tallas XS, S, M, L, XL, XXL.', '21151', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8378, 'Technical T-shirt for adults with an innovative and original print. Made of 135g/m2 polyester, breathable and soft to the touch. Raglan sleeves, round neck and available in sizes XS, S, M, L, XL, XXL.', '21151', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8379, 'Camiseta técnica para adulto sin mangas, con innovador y original estampado. Confeccionada en poliéster de 135g/m2, transpirable y de suave tacto. Manga ranglán, cuello redondo y disponible en tallas XS, S, M, L, XL, XXL.', '21152', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8380, 'Adult technical sleeveless shirt with innovative and original print. Made of 135g/m2 polyester, breathable and soft to the touch. Round neck and available in sizes XS, S, M, L, XL, XXL.', '21152', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8381, 'Polo técnico en material 100% poliéster piqué de 180g/m2. Fabricado en cómodo y suave tejido transpirable, con aperturas laterales en los bajos. De original diseño en color azul marino, con cuello y detalles de mangas en tono de contraste y a juego. Con 3 elegantes botones de cierre, a juego con el cuello y los detalles de las mangas. Disponible en varios colores y en tallas XS, S, M, L, XL, XXL.', '21153', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8382, 'Technical polo shirt in 100% polyester piqué material 180g/m2. Made in comfortable and soft breathable fabric, with side openings at the hems. Original design in navy blue, with collar and sleeve details in contrasting and matching colour. With 3 stylish button fastenings to match the collar and sleeve details. Available in various colours and in sizes XS, S, M, L, XL, XXL.', '21153', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8383, 'Polo técnico en material 100% poliéster de 160g/m2. Fabricado en cómodo y suave tejido transpirable, con aperturas laterales en los bajos. Cuello con 3 elegantes botones de cierre a juego. Disponible en variada gama de colores y en tallas XS, S, M, L, XL, XXL.', '21154', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8384, 'Technical polo shirt in 100% polyester material 160g/m2. Made of comfortable and soft breathable fabric, with side openings at the hem. Collar with 3 stylish buttons in matching colour. Available in wide range of colours and in sizes XS, S, M, L, XL, XXL.', '21154', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8385, 'Camiseta técnica unisex con original diseño a rayas, en combinación con tejido transpirable microperforado en axilas y laterales. Confeccionada en suave material 100% poliéster de 135g/m2, siendo una camiseta muy cómoda, ligera y transpirable; ideal para la práctica de deporte y también como prenda de uso diario. De corte moderno, manga ranglán y cuello redondo, con doble costura en dobladillo y mangas de doble tejido para una mayor durabilidad y resistencia al uso intensivo. Disponible en tallas XS, S, M, L, XL y XXL.', '21155', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8386, 'Unisex technical T-shirt with original striped design, in combination with breathable micro-perforated fabric in armpits and sides. Made of soft 100% polyester 135g/m2 material, being a very comfortable, light and breathable T-shirt, ideal for sports and also for everyday use. Modern cut, raglan sleeves and round neck, with double stitched hem and double fabric sleeves for greater durability and resistance to intensive use. Available in sizes XS, S, M, L, XL and XXL.', '21155', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8387, 'Camiseta técnica para adulto en 100% poliéster de 135g/m2. Fabricada en cómodo y suave tejido transpirable y con cuello redondo. Original diseño bicolor, con detalles en la parte inferior de las mangas y cuello en contraste de dos tonos. Disponible en tallas XS, S, M, L, XL, XXL.', '21156', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8388, 'Adult technical T-shirt in 100% polyester 135g/m2. Made of comfortable and soft breathable fabric with round neck. Original two-tone design, with details on the lower part of the sleeves and two-tone contrasting collar. Available in sizes XS, S, M, L, XL, XXL.', '21156', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8389, 'Camiseta técnica para adulto en material 100% poliéster transpirable de 135g/m2. Con manga ranglán, cuello redondo y con bandas frontales y laterales en tejido transpirable microperforado. Disponible en tallas S, M, L, XL, XXL.', '21157', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8390, 'Adult technical T-shirt in 100% breathable polyester material 135g/m2. With raglan sleeves, round neck, and front-side bands in breathable, micro-perforated fabric. Available in sizes S, M, L, XL, XXL.', '21157', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8391, 'Camiseta técnica para mujer en material 100% poliéster transpirable de 135g/m2. Con manga ranglán, cuello redondo y con bandas frontales y laterales en tejido transpirable microperforado. Disponible en tallas S, M, L, XL.', '21158', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8392, 'Woman technical T-shirt in 100% polyester breathable 135g/m2 material. With raglan sleeves, round neck and with front and side bands in breathable micro-perforated fabric. Available in sizes S, M, L, XL.', '21158', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8393, 'Camiseta técnica para niño en material 100% poliéster transpirable de 135g/m2. Con manga ranglán, cuello redondo y con bandas frontales y laterales en tejido transpirable microperforado. Disponible en tallas 4-5, 6-8, 10-12.', '21159', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8394, 'Kid technical T-shirt in 100% polyester breathable 135g/m2 material. With raglan sleeves, round neck and with front and side bands in breathable micro-perforated fabric. Available in sizes 4-5, 6-8, 10-12.', '21159', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (8395, 'Camiseta para adulto en material 100% poliéster transpirable de 160g/m2 con textura de algodón. Con cuello redondo y atrevido diseño con estampados inspirado en una paleta de pinturas. Disponible en tallas S, M, L, XL, XXL.', '21160', null);
